import { Container, Box } from "@chakra-ui/layout";
import { Button } from "@chakra-ui/react";
import axios from "axios";
import React, { useRef, useState } from "react";
import Images from "../Images/Images";

const Content = () => {
  const [img, setImg] = useState(null);

  const postImage = () => {
    console.log("here");
    axios
      .post("http://localhost:3002/newimage", { img })
      .then((response) => console.log(response));
  };

  return (
    <Container maxW="8xl" centerContent>
      <Box padding="4" width="80%">
        <Box style={{ width: "100%", alignItems: "center" }} mt="10">
          <input type="file" id="file" value={img}  onChange={(e) => setImg(e.target.files[0])}/>

          <Button bg="#677DB7" color="white" onClick={postImage}>
            Upload
          </Button>
        </Box>

        <Images />
      </Box>
    </Container>
  );
}
